mapper.delete:
delete from SeoPrice
where TaskId = #{taskid,jdbcType=BIGINT}
  and FromRank = #{fromrank,jdbcType=INTEGER}
  and ToRank = #{torank,jdbcType=INTEGER}

mapper.update:
update SeoPrice
set PriceOri = #{priceori,jdbcType=DECIMAL},
  Price = #{price,jdbcType=DECIMAL}
where TaskId = #{taskid,jdbcType=BIGINT}
  and FromRank = #{fromrank,jdbcType=INTEGER}
  and ToRank = #{torank,jdbcType=INTEGER}

mapper.select:
select TaskId, FromRank, ToRank, PriceOri, Price
from SeoPrice
where TaskId = #{taskid,jdbcType=BIGINT}
  and FromRank = #{fromrank,jdbcType=INTEGER}
  and ToRank = #{torank,jdbcType=INTEGER}

select n.TaskId, n.FromRank, n.ToRank, n.PriceOri, n.Price,m.UserId,m.CostCount
from SeoPrice n left join Seo m on n.TaskId = m.TaskId where FromRank = 1 and n.TaskId in

    #{taskid}
